import React, { useState, useEffect } from 'react';

import Helmet from '../components/Helmet';
import Section, { SectionBody, SectionTitle } from '../components/Section';
import Grid from '../components/Grid';
import ProductCard from '../components/ProductCard';
import ProductView from '../components/ProductView';

import productData from '../assets/fake-data/products';
import * as api from '../api/index';
import apiCaller from '../utils/apiCaller';

const Product = (props) => {
  const [product, setProducts] = useState(null);
  //   const product = productData.getProductBySlug(props.match.params.slug);

  const [userId, setUserId] = useState(null);
  console.log('slug is', props.match.params.slug);

  const [recommendProductIds, setRecommendProductIds] = useState(null);

  const [recommendProducts, setRecommendProducts] = useState(null);

  useEffect(() => {
    apiCaller(`api/book/${props.match.params.slug}`, 'get').then((res) => {
      setProducts(res.data);
    });

    let profile = localStorage.getItem('profile');
    let access_jwt_token = JSON.parse(profile)?.access_jwt_token;
    apiCaller('api/account/info', 'get', null, {
      authorization: access_jwt_token,
    }).then((res) => {
      // console.log('user id is:', res.data);
      setUserId(res.data?.user_id);
    });
  }, []);

  useEffect(() => {
    apiCaller(`api/MLData/${userId}`).then((res) => {
      // console.log('data recommdnd:', res.data?.yourData?.bookIds);
      setRecommendProductIds(res.data?.yourData?.bookIds);
    });
  }, [userId]);

  useEffect(async () => {
    console.log('recommedn product:', recommendProductIds);
    if (recommendProductIds) {
      let tempArray = [];
      Promise.all(
        recommendProductIds.map(async (item) => {
          await apiCaller(`api/book/${item}`, 'get').then(async (res) => {
            // console.log('gia tri cua recomend', res);
            // let dataProduct = res.data;
            await tempArray.push(res.data);
          });
        }),
      ).then(() => {
        setRecommendProducts(tempArray);
        // console.log('data temparray proudct', tempArray);
      });
    }
    // console.log('data recommend proudct', recommendProducts);
  }, [recommendProductIds]);

  let testData = [
    {
      book_id: 608,
      id: '008f0843-f664-4ee4-9e18-63b7c8c5d27b',
      name: 'Raw Vegan – Sức Mạnh Chữa Lành Của Thực Vật (Tặng kèm bookmark)',
      description:
        '<p>Thứ đắng nhất không phải là thuốc, mà là sự hối hận khi nhận ra đã quá muộn để thay đổi lối sống.</p>\n<p>Thứ đắt nhất không phải là tiền bạc, mà là thời gian tự do bạn phải đánh đổi để chống đỡ bệnh tật.</p>\n<p>Nhìn vào tỷ lệ người mắc ung thư mỗi năm, nhất là độ tuổi ngày càng trẻ hóa, nhất là khi đó là một người quen, người thân của bạn, bạn có giật mình không?</p>\n<p>Nhìn chính mình mỗi ngày đều thiếu sức sống, chỉ đủ sức chống chọi bệnh tật và những cảm xúc thất thường, không thể tập trung để làm việc, sáng tạo, cống hiến, bạn có thấy mình đang “sống mòn”?</p>\n<p>Thứ bạn nên thay đổi không phải là loại thuốc đắt hơn, bệnh viện tốt hơn, mà chính là LỐI SỐNG của bạn, bắt đầu từ việc ăn uống và sinh hoạt. Bởi “bạn là những gì bạn ăn, mỗi thực phẩm có một tần số riêng, và chỉ những thực phẩm mang tần số cao, giàu “năng lượng sống“ mới có thể nuôi dưỡng và nâng cấp cơ thể cũng như tâm trí bạn. </p>\n<p>“Raw vegan - sức mạnh chữa lành của thực vật” - công trình nghiên cứu công phu và đầy đủ nhất của TS. Norman W. Walker về chế độ ăn thuần thực vật và lối sống khoa học, là cuốn sách mà mỗi gia đình nên đặt trong tủ sách sức khỏe và căn bếp của mình để nhắc nhở mỗi người:</p>\n<ul>\n<li>Nhận ra những lợi ích quan trọng của chế độ ăn uống thuần thực vật trong việc chữa bệnh và hình thành một lối sống mới đơn giản, lành mạnh và vui khỏe mỗi ngày.</li>\n<li>“Giật mình” trước những thực phẩm độc hại mà chúng ta đang tiêu thụ và loại bỏ chúng từ dần dần đến vĩnh viễn khỏi thực đơn.</li>\n<li>Hiểu được cơ chế hoạt động của các tế bào, tuyến và nội tạng trong cơ thể, từ đó thiết lập lối sinh hoạt đúng nguyên lý cân bằng và thuận tự nhiên.</li>\n<li>Quan tâm đến sức khỏe tinh thần, hình thành tư duy sống trẻ,, bởi trạng thái tâm trí quyết định thể trạng của mỗi con người</li>\n<li>Tự kiểm chứng những kiến thức về sức khỏe, tự xây dựng thói quen kỷ luật và chịu trách nhiệm với chính mình.</li>\n</ul>\n<p>Bạn là người quyết định rằng bạn không chỉ "ổn", mà thực sự khỏe mạnh yêu đời, bạn không chỉ "tồn tại", mà đang sống trọn từng phút giây.<br /><br /></p><p>Giá sản phẩm trên Tiki đã bao gồm thuế theo luật hiện hành. Bên cạnh đó, tuỳ vào loại sản phẩm, hình thức và địa chỉ giao hàng mà có thể phát sinh thêm chi phí khác như phí vận chuyển, phụ phí hàng cồng kềnh, thuế nhập khẩu (đối với đơn hàng giao từ nước ngoài có giá trị trên 1 triệu đồng).....</p>',
      quantity: 9,
      price: 92000,
      image_url:
        'https://salt.tikicdn.com/cache/200x280/ts/product/4b/96/61/ac310ce3e6fa184067a9fcd935217b05.jpg',
      author: [
        {
          id: '9985b9c0-197c-40ed-800c-7f3f1f2c12be',
          name: 'Norman W.Walker',
        },
      ],
      category: [
        {
          category_id: 1,
          id: '2a50c1d3-7137-452e-8a83-29d53f2cbcc0',
          name: 'Sách tiếng Việt',
        },
      ],
    },
  ];

  React.useEffect(() => {
    window.scrollTo(0, 0);
  }, [product]);

  return (
    <Helmet title="title of books">
      <Section>
        <SectionBody>
          {product && <ProductView product={product} />}
        </SectionBody>
      </Section>
      <Section>
        <SectionTitle>Gợi ý riêng cho bạn</SectionTitle>
        <SectionBody>
          <Grid col={4} mdCol={2} smCol={1} gap={20}>
            {testData &&
              testData.map((item, index) => (
                <ProductCard
                  key={index}
                  img01={item.image_url}
                  img02={item.image_url}
                  name={item.name}
                  price={Number(item.price)}
                  slug={item.id}
                />
              ))}
          </Grid>
        </SectionBody>
      </Section>
    </Helmet>
  );
};

export default Product;
