const img1 = require('../images/slider/slide_1.png').default;
const img2 = require('../images/slider/slide_2.png').default;
const img3 = require('../images/slider/slide_3.png').default;

const heroSliderData = [
  {
    title: 'Cây Cam Ngọt Của Tôi.',
    description:
      'Được dịch từ tiếng Anh-My Sweet Orange Tree là một tiểu thuyết của José Mauro de Vasconcelos. Cuốn sách được xuất bản lần đầu tiên vào năm 1968 và được sử dụng cho các lớp học văn của các trường tiểu học ở Brazil. Nó cũng đã được dịch và xuất bản ở Mỹ và Châu Âu',
    img: img1,
    color: 'blue',
    path: '/catalog/ao-thun-dinosaur-01',
  },
  {
    title: 'Đi tìm lẽ sống',
    description: '',
    img: img2,
    path: '/catalog/ao-thun-dinosaur-02',
    color: 'pink',
  },
  {
    title: 'Đi tìm lẽ sống',
    description:
      '“Đi tìm lẽ sống là một cuốn sách ghi lại những trải nghiệm của Victor Frankl trong trại tập trung Auschwitz. Nó chắc chắn sẽ giúp độc giả có cách nhìn mới về lý do mà mình tồn tại.',
    img: img3,
    path: '/catalog/ao-thun-dinosaur-03',
    color: 'orange',
  },
];

export default heroSliderData;
