const img1 = require('../images/slider/slide_1.png').default;
const img2 = require('../images/slider/slide_2.png').default;
const img3 = require('../images/slider/slide_3.png').default;

const heroSliderData = [
  {
    title: 'Cây Cam Ngọt Của Tôi.',
    description:
      'Được dịch từ tiếng Anh-My Sweet Orange Tree là một tiểu thuyết của José Mauro de Vasconcelos. Cuốn sách được xuất bản lần đầu tiên vào năm 1968 và được sử dụng cho các lớp học văn của các trường tiểu học ở Brazil. Nó cũng đã được dịch và xuất bản ở Mỹ và Châu Âu',
    img: img1,
    color: 'blue',
    path: '/catalog/ao-thun-dinosaur-01',
  },
  {
    title: 'Rèn luyện tư duy phản biện',
    description:
      '“Em đồng ý ra nước ngoài sinh con. Em sẽ sang Mỹ cùng với mẹ Mơ, ở đúng ngôi nhà ngày xưa anh từng ở khi học đại học. Em muốn có vệ sĩ đi theo bảo vệ, được không?” Niệm gật đầu trước tất cả những yêu cầu oái oăm của Miên. Có người nói Miên thật may mắn, bởi vì cô từng hẹn hò với Chủ tịch Tập đoàn Nhất Kỷ nên sau khi chia tay, cô được hưởng nhiều đặc quyền hơn bình thường. Liệu có ai đó trong số các bạn cảm thấy rằng chia tay một người… cắt phăng đi cả một phần tuổi trẻ… là một loại chuyện may mắn hay không?',
    img: img2,
    path: '/catalog/ao-thun-dinosaur-02',
    color: 'pink',
  },
  {
    title: 'Polo Nữ Coolmax Lacoste',
    description:
      'Mẫu áo polo nữ được làm bằng chất liệu coolmax đem lại cảm giác mát lạnh khi mặc. Thiết kế mẫu áo polo coolmax này có kiểu dáng cực kỳ thoải mái. Tạo sự gọn gàng hứa hẹn sẽ là mẫu áo polo vô cùng hot trong thời điểm sắp tới',
    img: img3,
    path: '/catalog/ao-thun-dinosaur-03',
    color: 'orange',
  },
];

export default heroSliderData;
